import React from 'react';
import './slider.css';
import PersonPicture from '../../images/PersonPicture.png'
import Foto1 from '../../images/Foto1.jpg'
import BackgroundSlide from '../../images/BackgroundSlide.jpg'

/*const SecondBanner = (props) => {
    return (
        <h2>Segundo banner{props.descricao}</h2>
    )
};*/

const SliderApresentation = () => {
    return (
        <div id="body">
            <div class="slider">
                <div class="slides">
                    <input type="radio" name="radio-btn" id="radio1"></input>
                    <input type="radio" name="radio-btn" id="radio2"></input>
                    <input type="radio" name="radio-btn" id="radio3"></input>

                    <div class="first-slide">
                        <img src={PersonPicture} class="person-picture"></img>
                        <img src={Foto1} class="Foto1"></img>
                    </div>

                    <div class="second-slide">

                    </div>

                    <div class="third-slide">

                    </div>

                    <div class="auto-navigation">
                        <div class="auto-btn1"></div>
                        <div class="auto-btn2"></div>
                        <div class="auto-btn3"></div>
                    </div>

                    <div class="manual-navigation">
                        <label for="radio1" class="manual-btn"></label>
                        <label for="radio2" class="manual-btn"></label>
                        <label for="radio3" class="manual-btn"></label>
                    </div>
                </div>
            </div>
        </div>
    )
};

/*export default SliderApresentation;*/
export default SliderApresentation;
