import React from 'react';
import './slider.css';
import PersonPicture from '../../images/PersonPicture.png'
import Foto1 from '../../images/Foto1.jpg'
import Foto2 from '../../images/Foto2.jpg'
import Foto3 from '../../images/Foto3.jpg'
import BackgroundSlide from '../../images/BackgroundSlide.jpg'

/*const SecondBanner = (props) => {
    return (
        <h2>Segundo banner{props.descricao}</h2>
    )
};*/

const SliderApresentation = () => {
    return (
        <div id="body">
            <ul class="slider">
                <li>
                    <input type="radio" name="radio-btn" id="slide1"></input>
                    <label for="slide1"></label>
                    <img src={PersonPicture} class="PersonPicture1"></img>
                </li>
                <li>
                    <input type="radio" name="radio-btn" id="slide2"></input>
                    <label for="slide2"></label>
                    <img src={PersonPicture} class="PersonPicture2"></img>
                </li>
                <li>
                    <input type="radio" name="radio-btn" id="slide3"></input>
                    <label for="slide3"></label>
                    <img src={PersonPicture} class="PersonPicture3"></img>
                </li>
            </ul>
        </div>
    )
};

/*export default SliderApresentation;*/
export default SliderApresentation;
